INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (1, N'Think Leadership 2017', N'2017-06-11 00:00:00', N'Think Leadership este evenimentul dedicat profesionistilor cu experienta in marketing, sales, finance, IT, logistica sau resurse umane care urmaresc sau detin o pozitie de manager. Acesta isi propune sa ofere un spatiu de networking pentru profesionisti si lideri din mediul business de la care tu sa descoperi ce resurse poti accesa pentru dezvoltarea ta profesionala intr-o companie de top.
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (2, N'Mixul dintre Design Thinking & Agile pentru a dezvolta produse orientate spre userii lor', N'2017-06-12 00:00:00', N'Silvia Mihăilescu va modera și va coordona sesiunea dedicată studiilor de caz marca IBM, în contextul dezvoltării produselor centrate pe utilizatori, prin combinarea Design Thinking și a abordării Agile.', N'Training', N'https://image.freepik.com/free-vector/creative-team_23-2147506557.jpg', 120, 7)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (3, N'iCEE.fest', N'2017-06-15 00:00:00', N'iCEE.fest este unul dintre cele mai apreciate evenimente de profil din regiunea Europei Centrale și de Est, devenind un real festival al industriei digitale în cei 5 ani de când a avut loc prima ediție.', N'Conferinta', N'http://www.controlesempresariales.com/es-co/Blog/PublishingImages/caracters03.jpg', 60, 2)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (4, N'Regatta IT', N'2017-09-16 00:00:00', N'Regatta IT anunță anul acesta cea de-a doua ediție a competiției de sailing pentru pasionații de tehnologie. Oameni din companii de IT, care iubesc adrenalina, spiritul competiției și ideea de a socializa într-un mediu informal, sunt așteptați și anul acesta pe mare, la un eveniment dedicat lor: cu distracție, soare, vânt atât cât trebuie! Neapărat în echipă, ca să putem să ocupăm frumos podiumul și clasamentul, dotați cu energie și zâmbete!', N'Competitie', N'https://image.shutterstock.com/z/stock-vector-programmer-and-process-coding-programming-concept-264265364.jpg', 180, 6)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (5, N'FII Practic', N'2017-03-05 00:00:00', N'Proiectul urmăreşte oferirea unor training-uri calitative, moderne şi practice studenţilor şi liceenilor ieşeni, în arii relevante din domeniul IT şi din domeniul project & business management.', N'Training', N'http://360.uaic.ro/wp-content/uploads/2014/01/Sigl%C4%83_FIIPractic.jpg', 120, 1)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (6, N'FII Code', N'2017-03-05 00:00:00', N'Proiectul dorește ca participanții să valorifice abilitățile (și să dezvolte competențe), prin intermediul competiției: gândirea algoritmică și analitică, modelarea și implementarea, dezvoltarea de noi tehnologii, creativitatea, adaptabilitatea, precum  și capacitatea de a susține în public a unei lucrări proprii. Cele 3 secțiuni, Algoritmică, Web și Mobile, sunt concepute special pentru acest lucru, fiecare acoperind o anumită arie din domeniul IT aflată în acest moment în plina expansiune. Modul de organizare al competiției implică cunoașterea atât de noțiuni practice cât și teoretice. ', N'Competitie', N'https://ziar.asii.ro/wp-content/uploads/2017/05/fiicode-300x300.png', 180, 3)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (7, N'STAGII DE PRACTICĂ', N'2017-03-18 00:00:00', N'Programul național de stagiatură Stagii pe Bune propune plasarea studenților facultaților cu profil IT&C în stagii de practică, desfășurate pe durata verii, în companiile din domeniul IT&C. Programul de stagii se află la a treisprezecea ediție și este, și în acest an, programul oficial de stagiatură al celor mai importante centre educaționale IT&C din România: Iași, București.', N'Conferinta', N'http://inventeaza.ro/sites/default/files/styles/news_article_picture/public/stagiipebune.jpg?itok=vcUVbtwe', 60, 4)
