INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (11, N'Chief Data & Analytics Officer Singapore', N'2017-07-26 00:00:00', N'Chief Data and Analytics Officer Singapore is the most comprehensive event for data development, analytics and innovation across the region. Bringing you two full days of insight, conversation and expert-led masterclasses provide excellent opportunity to further expand your knowledge and tool set.', N'Conferinta', N'http://www.dataversity.net/wp-content/uploads/2014/08/cdo.jpg?x23053', 60, 8)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (12, N'FinTech Connect 2017', N'2017-12-06 00:00:00', N'Combining the hustle and bustle of an exhibition featuring over 3000 visitors, and 200 exhibitors and partners from over 50 countries, FinTech Connect Live is the UK’s largest fintech event. Playing host to 4 strategic conference sessions with inspirational case studies from around the world, a technology buyers theatre with 50 product demos, 12 educational workshops tackling practical fast growth challenges, and two full days of dedicated mentoring clinics for start up leaders, all brought to you from over 300 of the industries finest speakers, FinTech Connect Live is the ‘must have ticket’ for stakeholders from across the full fintech eco-system.', N'Conferinta', N'http://cdn.ttgtmedia.com/Marketing/2017/05/FinTech_logo.png', 60, 2)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (13, N'Smarter Business Tech LIVE 2017', N'2017-12-16 00:00:00', N'Introducing Smarter Business Tech LIVE, the brand new 2-day business technology solutions event, designed for the small and mid-size enterprise sector providing crucial knowledge, intelligence and technology/partner options empowering business leaders, C-suite & enterprise decision makers plus departmental and change management. Delivering business growth & success through technology-enabled solutions and partners across all departments including IT, Sales, Marketing, Finance, HR, Logistics, Supply Chain, Commerce, Production, Service, Enterprise Planning… and more', N'Conferinta', N'https://yt3.ggpht.com/-AJRMPswa9J8/AAAAAAAAAAI/AAAAAAAAAAA/11EbN7DTM3w/s900-c-k-no-mo-rj-c0xffffff/photo.jpg', 60, 6)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (14, N'Cloud & DevOps World 2017', N'2017-06-15 00:00:00', N'This year Cloud & DevOps World will be better than ever. It will be part of London Tech Week’s Official expo, TechXLR8. Bringing you industry leading organisations all under one roof. With the likes of Facebook, PayPal, HSBC, Chef and any more.', N'Conferinta', N'https://cdn-az.allevents.in/banners/a63d9f121dfc59f51f4b57cdf91639ce', 60, 9)
INSERT INTO [dbo].[Activities] ([Id], [Name], [StartDate], [Description], [Type], [Image], [Minutes], [Skill_Id]) VALUES (15, N'DataCentre Transformation Manchester (DT Manchester)', N'2017-06-10 02:30:00', N'Now in its seventh year, Data Centre Transformation Manchester (DT Manchester) is organised by Angel Business Communications, in association with Datacentre Solutions, the University of Leeds and the Data Centre Alliance. The combination of such data centre knowledge and expertise ensures that DT Manchester is the premier data centre education event in the calendar, bringing together the data centre research and design community, the data centre vendor industry and, most importantly, enterprise end users, for a collaborative information exchange.', N'Conferinta', N'http://cdn.ttgtmedia.com/Marketing/2017/03/DT%20Manchester%20Logo%202017%20Dated.jpg', 60, 8)
